import { useState } from "react";
import { motion } from "framer-motion";

const Project = ({ title, description, video }) => {
  const [hovered, setHovered] = useState(false);

  return (
    <motion.div 
      className="relative bg-white/10 backdrop-blur-lg p-6 rounded-xl transition-all duration-300 hover:scale-105"
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      <h3 className="text-white text-lg">{title}</h3>
      <p className={`text-white transition-opacity ${hovered ? "opacity-0" : "opacity-100"}`}>{description}</p>
      {hovered && (
        <motion.video
          autoPlay loop muted 
          className="absolute inset-0 w-full h-full object-cover rounded-xl opacity-0"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <source src={video} type="video/mp4" />
        </motion.video>
      )}
    </motion.div>
  );
};

const Projects = () => {
  return (
    <section id="projects" className="grid grid-cols-4 gap-6 p-10">
      <Project 
        title="Vehicle System"
        description="A car system with Raycast and BodyForces."
        video="/assets/project1.mp4"
      />
      <Project 
        title="Physics Sandbox"
        description="An experimental physics playground."
        video="/assets/project2.mp4"
      />
    </section>
  );
};

export default Projects;
