import { useState, useEffect } from "react";

const Header = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 50);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header
      className={`fixed top-5 left-1/4 w-1/2 p-4 backdrop-blur-lg bg-white/10 rounded-2xl transition-all duration-300 ${
        scrolled ? "rounded-none w-full top-0 left-0" : ""
      }`}
    >
      <div className="flex justify-between items-center">
        <h1 className="text-white font-signature text-3xl">Drxck</h1>
        <nav>
          <ul className="flex gap-6 text-white">
            <li><a href="#about">About</a></li>
            <li><a href="#projects">Projects</a></li>
            <li><a href="#contact">Contact</a></li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
